mapper.select:
select * from (
	select rownum as RNUM, A.* from	(
		select * from noticeview
		
		order by notice_no
	)A
)

where RNUM> #{firstRecordIndex} 
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select * from (
	select rownum as RNUM, A.* from	(
		select * from faqview
		
		order by faq_no
	)A
)

where RNUM> #{firstRecordIndex} 
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select * from (
	select rownum as RNUM, A.* from	(
		select * from boardview
		
		order by board_group_no desc, board_sort
	)A
)

where RNUM> #{firstRecordIndex} 
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}
