mapper.select:
select * from
(
    select rownum RNUM,a.* from
    (
        select * from education_place 
        
        order by ep_no desc
    )a
)

where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select * from
(
    select rownum as RNUM, a.* from
    (
        select m.*,(select count(*) from education_application e where e.edu_no=m.edu_no) qty,
        (select ep_name from education_place e where e.ep_no=m.ep_no) epName
        from mbti_education m
        
        order by m.edu_no desc
    )a
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update mbti_education
set edu_name = #{eduName}, edu_teacher = #{eduTeacher}, edu_com = #{eduCom}, edu_people_number = #{eduPeopleNumber}, edu_price = {eduPrice}, ep_name = #{epName} 
where edu_no = #{eduNo}

mapper.select:
select * from
(
    select rownum RNUM,a.* from
    (
        select * from education_teacher 
        
        order by edu_tea_no desc
    )a
)

where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select * from
(
	select rownum RNUM, a.* from
	(
		select e.*,(select count(*) from education_application app where e.edu_no=app.edu_no) qty
		from educationview e
		
		order by edu_app_no desc
	)a
)

where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}
