mapper.select:
select pwd from member
where userid = #{userid}
and outdate is null

mapper.update:
update member
set email1=#{email1}, email2=#{email2}, hp1=#{hp1}, hp2=#{hp2},
	hp3=#{hp3},
	zipcode=#{zipcode}, address=#{address}, addressDetail=#{addressDetail}
where userid=#{userid}

update member
set outdate=sysdate
where userid=#{userid}
