mapper.select:
select * 
from
(
	select rownum as RNUM, A.*
	 from
	(
		select * from member
		
		order by no desc
	)A
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select userid from member
where name = #{name}
and email = #{email}

select pwd from member
where userid=#{userid}
and outdate is null

mapper.update:
update member
set email=#{email}, hp=#{hp}, zipcode=#{zipcode}, address=#{address}, address_Detail=#{addressDetail}
where userid=#{userid}

update member
set outdate=sysdate
where userid=#{userid}

mapper.select:
   		select userid from member
		where name= #{name} 
		and email= #{email}

   	select userid from member
	where name= #{name}
	and hp=#{hp}

select email 
from member
where userid=#{userid} and email=#{email}

select pwd from member
where userid = #{userid}
and outdate is null

mapper.update:
update member
set pwd=#{pwd}
where userid=#{userid}

mapper.select:
   		select count(*) from member
		where userid=#{userid} and name=#{name}
		and outdate is null
