mapper.select:
select * 
from
(
	select rownum as RNUM, A.*
	 from
	(
		select * from member
		
		order by no 
	)A
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select userid from member
where name = #{name}
and email = #{email}

select * from member
where name=#{name} 
and hp=#{hp}
